#import "ViewController.h"
#import "DWCoreTextLabel.h"
@interface ViewController ()

@property (nonatomic ,strong) DWCoreTextLabel * label;

@end

@implementation ViewController

- (void)viewDidLoad {
    [super viewDidLoad];
    
    DWCoreTextLabel * label = [[DWCoreTextLabel alloc] initWithFrame:self.view.bounds];
//    label.center = self.view.center;
    label.textAlignment = NSTextAlignmentRight;
//    label.textVerticalAlignment = DWTextVerticalAlignmentTop;
    label.text = @"";
    label.textInsets = UIEdgeInsetsMake(10, 10, 10, 10);
//    label.numberOflines = 3;
//    label.attributedText = [[NSAttributedString alloc] initWithString:@"我们"];
    label.textColor = [UIColor blueColor];
    [self.view addSubview:label];
    label.exclusionPaths = @[[UIBezierPath bezierPathWithRect:CGRectMake(10, 10, 100, 100)],[UIBezierPath bezierPathWithOvalInRect:CGRectMake(100, 350, 200, 100)]].mutableCopy;
    [label dw_InsertImage:[UIImage imageNamed:@"2.jpg"] size:CGSizeMake(20, 20) padding:25 descent:0 atLocation:400 target:self selector:@selector(click)];
    [label dw_AddTarget:self selector:@selector(click) toRange:NSMakeRange(200, 50)];
    [label dw_DrawImage:[UIImage imageNamed:@"2.jpg"] WithPath:[UIBezierPath bezierPathWithOvalInRect:CGRectMake(100, 200, 100, 200)] margin:5 drawMode:(DWTextImageDrawModeSurround) target:self selector:@selector(click)];
    NSDictionary * dic = @{NSForegroundColorAttributeName:[UIColor redColor]};
    label.activeTextAttributes = dic;
    NSDictionary * dic2 = @{NSForegroundColorAttributeName:[UIColor greenColor]};
    label.activeTextHighlightAttributes = dic2;
    [label dw_AddTarget:self selector:@selector(second) toRange:NSMakeRange(125, 50)];
    self.label = label;
//    [self.label sizeToFit];
}


-(void)click
{
    NSLog(@"click");
}

-(void)second
{
    NSDictionary * dic2 = @{NSForegroundColorAttributeName:[UIColor purpleColor]};
    self.label.activeTextAttributes = dic2;
}
- (void)didReceiveMemoryWarning {
    [super didReceiveMemoryWarning];
    // Dispose of any resources that can be recreated.
}


@end
